con:response.con:script:
def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}
requestContext.PTID = generator( (('A'..'Z')+('0'..'9')+('-')).join(), 40 )

con:mockOperation.con:dispatchPath:
def request = new XmlSlurper().parseText(mockRequest.requestContent)
def redirectURL = request.Body.Reserve.dialog.wdycf.toString()
def price = request.Body.Reserve.purchase.productDetail.price.toString()
requestContext.redirectURL = redirectURL
context.price = price

